import { Request, Response, NextFunction } from 'express';
import { doubleCsrf } from 'csrf-csrf';
import { randomBytes } from 'crypto';

const SECRET = process.env.CSRF_SECRET || randomBytes(32).toString('hex');

const csrfUtils = doubleCsrf({
  getSecret: () => SECRET,
  cookieName: 'x-csrf-token',
  cookieOptions: {
    httpOnly: true,
    sameSite: process.env.NODE_ENV === 'production' ? 'strict' : 'lax',
    secure: process.env.NODE_ENV === 'production',
    path: '/',
  },
  size: 64,
  ignoredMethods: ['GET', 'HEAD', 'OPTIONS'],
  getCsrfTokenFromRequest: (req: Request) => {
    // Check headers first
    const headerToken = req.headers && req.headers['x-csrf-token'];
    if (headerToken) return headerToken as string;

    // Then check body
    const bodyToken = req.body && req.body.csrfToken;
    if (bodyToken) return bodyToken;

    // Finally check query params
    return req.query && req.query._csrf as string;
  },
  getSessionIdentifier: (req: Request) => req.sessionID || '',
});

export const csrfProtection = csrfUtils.doubleCsrfProtection;

export const generateCsrfToken = (req: Request, res: Response, next: NextFunction) => {
  const token = csrfUtils.generateCsrfToken(req, res);
  res.setHeader('X-CSRF-Token', token);
  next();
};


export const validateCsrfToken = (req: Request, res: Response, next: NextFunction) => {
  if (process.env.NODE_ENV === 'development') {
    return next();
  }

  try {
    if (['POST', 'PUT', 'DELETE', 'PATCH'].includes(req.method)) {
      csrfUtils.validateRequest(req);
    }
    next();
  } catch (error) {
    console.error('CSRF validation failed:', error);
    res.status(403).json({ message: 'Invalid CSRF token' });
  }
};

server/middleware/error.ts.content:
import { Request, Response, NextFunction } from 'express';
import { ZodError } from 'zod';
import { logError } from '../utils/logger';

export const errorHandler = (err: Error, req: Request, res: Response, next: NextFunction) => {
  // Log the error
  logError('Error occurred', {
    error: err.message,
    stack: err.stack,
    method: req.method,
    path: req.path,
    userId: req.session?.userId,
    timestamp: new Date()
  });

  // Handle specific error types
  if (err instanceof ZodError) {
    return res.status(400).json({
      message: 'Validation failed',
      errors: err.errors
    });
  }

  if (err.name === 'UnauthorizedError') {
    return res.status(401).json({
      message: 'Authentication required'
    });
  }

  if (err.name === 'ForbiddenError') {
    return res.status(403).json({
      message: 'Access denied'
    });
  }

  // Handle CSRF errors
  if (err.name === 'CSRFTokenError') {
    return res.status(403).json({
      message: 'Invalid CSRF token'
    });
  }

  // Handle file upload errors
  if (err.name === 'MulterError') {
    return res.status(400).json({
      message: 'File upload error',
      error: err.message
    });
  }

  // Handle database errors
  if (err.name === 'DatabaseError') {
    return res.status(500).json({
      message: 'Database error occurred'
    });
  }

  // Default error response
  const statusCode = res.statusCode !== 200 ? res.statusCode : 500;
  res.status(statusCode).json({
    message: process.env.NODE_ENV === 'production' 
      ? 'An unexpected error occurred' 
      : err.message
  });
};

server/middleware/rate-limit.ts.content:
import rateLimit from 'express-rate-limit';
import RedisStore from 'rate-limit-redis';
import { redisClient } from '../config/redis.js';
import { logSecurity } from '../utils/logger.js';


// Base rate limit configuration
const baseConfig = {
  windowMs: parseInt(process.env.RATE_LIMIT_WINDOW_MS || '900000'), // 15 minutes
  max: parseInt(process.env.RATE_LIMIT_MAX_REQUESTS || '100'),
  standardHeaders: true,
  legacyHeaders: false,
  store: new RedisStore({
    sendCommand: (...args: string[]) => redisClient.sendCommand(args),
    prefix: 'rate-limit:'
  }),
};

// General API rate limiter
export const apiLimiter = rateLimit({
  ...baseConfig,
  skip: (req) => {
    // Skip localhost requests, useful for internal health checks or when running behind a proxy
    const ip = req.ip;
    return ip === '127.0.0.1' || ip === '::1';
  },
  handler: (req, res) => {

    logSecurity('Rate limit exceeded', {
      type: 'RATE_LIMIT_EXCEEDED',
      ip: req.ip,
      path: req.path,
      headers: req.headers,
    });
    res.status(429).json({
      error: 'Too many requests, please try again later',
      retryAfter: Math.ceil(baseConfig.windowMs / 1000),
    });
  },
});

// Stricter auth routes rate limiter
export const authLimiter = rateLimit({
  ...baseConfig,
  windowMs: parseInt(process.env.AUTH_RATE_LIMIT_WINDOW_MS || '900000'),
  max: parseInt(process.env.AUTH_RATE_LIMIT_MAX || '5'),
  skip: (req) => {
    // Skip localhost requests
    const ip = req.ip;
    return ip === '127.0.0.1' || ip === '::1';
  },
  handler: (req, res) => {

    logSecurity('Authentication rate limit exceeded', {
      type: 'AUTH_RATE_LIMIT_EXCEEDED',
      ip: req.ip,
      path: req.path,
      headers: req.headers,
    });
    res.status(429).json({
      error: 'Too many authentication attempts, please try again later',
      retryAfter: Math.ceil(baseConfig.windowMs / 1000),
    });
  },
});

// Specific endpoint rate limiters
export const createEndpointLimiter = (options: {
  windowMs?: number;
  max?: number;
  message?: string;
}) => {
  return rateLimit({
    ...baseConfig,
    ...options,
    handler: (req, res) => {
      logSecurity('Endpoint rate limit exceeded', {
        type: 'ENDPOINT_RATE_LIMIT_EXCEEDED',
        ip: req.ip,
        path: req.path,
        headers: req.headers,
      });
      res.status(429).json({
        error: options.message || 'Too many requests, please try again later',
        retryAfter: Math.ceil((options.windowMs || baseConfig.windowMs) / 1000),
      });
    },
  });
};

server/middleware/security.ts.content:
import { Request, Response, NextFunction } from 'express';
import rateLimit from 'express-rate-limit';
import helmet from 'helmet';
import cors from 'cors';
import type { CorsOptions } from 'cors';
import { z } from 'zod';
import { doubleCsrf } from 'csrf-csrf';
import crypto from 'crypto';

// Declare session properties
declare module 'express-session' {
  interface SessionData {
    userId: string;
    userRole: string;
    id: string;
    lastActive: Date;
    ipAddress: string;
  }
}

// Encryption configuration
const ENCRYPTION_KEY = process.env.ENCRYPTION_KEY || crypto.randomBytes(32);
const IV_LENGTH = 16;

// Encryption utility for sensitive data
export function encrypt(text: string): string {
  const iv = crypto.randomBytes(IV_LENGTH);
  const cipher = crypto.createCipheriv('aes-256-cbc', Buffer.from(ENCRYPTION_KEY), iv);
  let encrypted = cipher.update(text, 'utf8', 'hex');
  encrypted += cipher.final('hex');
  return `${iv.toString('hex')}:${encrypted}`;
}

export function decrypt(text: string): string {
  try {
    const [ivHex, encryptedHex] = text.split(':');
    if (!ivHex || !encryptedHex) {
      throw new Error('Invalid encrypted text format');
    }
    const iv = Buffer.from(ivHex, 'hex');
    const decipher = crypto.createDecipheriv('aes-256-cbc', Buffer.from(ENCRYPTION_KEY), iv);
    let decrypted = decipher.update(encryptedHex, 'hex', 'utf8');
    decrypted += decipher.final('utf8');
    return decrypted;
  } catch (error) {
    throw new Error('Failed to decrypt data');
  }
}

// CSRF Protection
const csrfUtils = doubleCsrf({
  getSecret: () => process.env.CSRF_SECRET || 'your-secret-key',
  cookieName: 'x-csrf-token',
  getCsrfTokenFromRequest: (req: Request) => req.headers['x-csrf-token'] as string,
  getSessionIdentifier: (req: Request) => req.session?.id || '',
  size: 64,
  ignoredMethods: ['GET', 'HEAD', 'OPTIONS'],
  cookieOptions: {
    httpOnly: true,
    sameSite: 'strict',
    secure: process.env.NODE_ENV === 'production',
  },
});

export const csrfMiddleware = (req: Request, res: Response, next: NextFunction) => {
  try {
    if (req.method === 'GET') {
      const token = csrfUtils.generateCsrfToken(req, res);
      // The cookie is automatically set by generateCsrfToken
      next();
    } else {
      csrfUtils.validateRequest(req);
      next();
    }
  } catch (error) {
    res.status(403).json({ message: 'Invalid CSRF token' });
  }
};

// Rate limiting middleware
export const rateLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 100, // Limit each IP to 100 requests per windowMs
  message: 'Too many requests from this IP, please try again later',
});

// Stricter auth route limiter
export const authLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 5, // 5 attempts per window per IP
  message: 'Too many login attempts, please try again later',
  handler: (req: Request, res: Response) => {
    console.error(`Auth rate limit exceeded for IP ${req.ip}`);
    res.status(429).json({
      message: 'Too many login attempts, please try again later',
    });
  },
});

// IP blocking middleware for suspicious activity
const blockedIPs = new Set<string>();
const suspiciousAttempts = new Map<string, number>();

export const ipFilter = (req: Request, res: Response, next: NextFunction) => {
  const ip = req.ip || req.socket.remoteAddress;

  if (!ip) {
    console.error('No IP address found in request');
    return res.status(400).json({ message: 'Bad request' });
  }

  if (blockedIPs.has(ip)) {
    console.error(`Blocked IP attempt: ${ip}`);
    return res.status(403).json({ message: 'Access denied' });
  }

  // Track suspicious attempts
  const attempts = suspiciousAttempts.get(ip) || 0;
  if (attempts > 10) { // Block after 10 suspicious attempts
    blockedIPs.add(ip);
    suspiciousAttempts.delete(ip);
    console.error(`IP blocked due to suspicious activity: ${ip}`);
    return res.status(403).json({ message: 'Access denied' });
  }

  // Increment attempts
  suspiciousAttempts.set(ip, attempts + 1);

  next();
};

// CORS configuration
const corsOptions: CorsOptions = {
  origin: process.env.NODE_ENV === 'production' 
    ? ['https://familypeace.org'] 
    : ['http://localhost:5173'],
  credentials: true,
  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
  allowedHeaders: ['Content-Type', 'Authorization', 'x-csrf-token'],
  maxAge: 600 // 10 minutes
};

export const corsMiddleware = cors(corsOptions);

// Sanitize error messages
export function sanitizeError(error: any): { message: string; code?: string } {
  // Only include error details in development
  if (process.env.NODE_ENV === 'development') {
    return {
      message: error.message || 'An error occurred',
      code: error.code
    };
  }
  // Generic error message in production
  return {
    message: 'An error occurred',
    code: 'INTERNAL_ERROR'
  };
}

// Helmet configuration with CSP
export const helmetMiddleware = helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'", "https://www.paypal.com", "https://www.sandbox.paypal.com"],
      styleSrc: ["'self'", "'unsafe-inline'"],
      imgSrc: ["'self'", "data:", "https:"],
      connectSrc: ["'self'", "https://api.paypal.com", "https://api.sandbox.paypal.com"],
      frameSrc: ["'self'", "https://www.paypal.com", "https://www.sandbox.paypal.com"],
      upgradeInsecureRequests: [],
    },
  },
  crossOriginEmbedderPolicy: false, // Required for PayPal SDK
  crossOriginOpenerPolicy: { policy: "same-origin-allow-popups" }, // Required for PayPal popup
  crossOriginResourcePolicy: { policy: "cross-origin" }, // Required for external resources
});

// Helmet security headers
export const securityHeaders = helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'", 'https:'],
      scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'", 'https:'],
      imgSrc: ["'self'", 'data:', 'https:'],
      connectSrc: ["'self'", 'https:', 'wss:'],
      fontSrc: ["'self'", 'https:', 'data:'],
      objectSrc: ["'none'"],
      mediaSrc: ["'self'"],
      frameSrc: ["'none'"],
    },
  },
});

// Input validation middleware
export function validateInput<T extends z.ZodType>(schema: T) {
  return async (req: Request, res: Response, next: NextFunction) => {
    try {
      const validatedData = await schema.parseAsync({
        body: req.body,
        query: req.query,
        params: req.params,
      });

      // Add validated data to request object
      req.validatedData = validatedData;
      next();
    } catch (error) {
      if (error instanceof z.ZodError) {
        res.status(400).json({
          message: 'Validation failed',
          errors: error.errors,
        });
        return;
      }
      next(error);
    }
  };
}

// Session activity tracking middleware
export function trackSessionActivity(req: Request, res: Response, next: NextFunction) {
  if (req.session) {
    const currentTime = new Date();
    const lastActive = req.session.lastActive || currentTime;
    const currentIp = req.ip || req.socket.remoteAddress || 'unknown';

    // Check for session timeout (30 minutes)
    const timeoutMs = 30 * 60 * 1000; // 30 minutes in milliseconds
    if (currentTime.getTime() - lastActive.getTime() > timeoutMs) {
      req.session.destroy((err) => {
        if (err) console.error('Session destruction error:', err);
      });
      return res.status(440).json({ message: 'Session expired' });
    }

    // The IP address check for session hijacking has been removed.
    // In a production environment with load balancers or proxies, the internal IP can change
    // between requests for the same user, causing false positives and invalidating sessions.
    // if (req.session.ipAddress && req.session.ipAddress !== currentIp) {
    //   console.error(`Potential session hijacking detected: IP changed from ${req.session.ipAddress} to ${currentIp}`);
    //   req.session.destroy((err) => {
    //     if (err) console.error('Session destruction error:', err);
    //   });
    //   return res.status(403).json({ message: 'Session invalidated' });
    // }


    // Update session
    req.session.lastActive = currentTime;
    req.session.ipAddress = currentIp;
  }
  next();
}

// Authentication middleware
export function requireAuth(req: Request, res: Response, next: NextFunction) {
  if (!req.session || !req.session.userId) {
    res.status(401).json({ message: 'Unauthorized' });
    return;
  }

  // Verify session integrity
  if (!req.session.userRole || !req.session.id) {
    req.session.destroy((err) => {
      if (err) console.error('Session destruction error:', err);
    });
    return res.status(401).json({ message: 'Invalid session' });
  }

  next();
}

// Role-based authorization middleware
export function requireRole(roles: string[]) {
  return (req: Request, res: Response, next: NextFunction) => {
    if (!req.session || !req.session.userRole) {
      res.status(401).json({ message: 'Unauthorized' });
      return;
    }

    if (!roles.includes(req.session.userRole)) {
      res.status(403).json({ message: 'Forbidden' });
      return;
    }

    next();
  };
}

// Error handling middleware
export function errorHandler(err: Error | any, req: Request, res: Response, next: NextFunction) {
  console.error(err?.stack || err);

  if (err?.name === 'UnauthorizedError') {
    res.status(401).json({ message: 'Invalid token' });
    return;
  }

  if (err?.code === 'EBADCSRFTOKEN') {
    res.status(403).json({ message: 'Invalid CSRF token' });
    return;
  }

  res.status(500).json({
    message: process.env.NODE_ENV === 'production'
      ? 'Internal server error'
      : err?.message || 'An error occurred'
  });
}

