});

// Get all events
router.get('/events', async (req: Request, res: Response, next: NextFunction) => {
  try {
    const eventsList = await db
      .select({
        id: events.id,
        name: events.name,
        description: events.description,
        date: events.date,
        endDate: events.endDate,
