import speakeasy from 'speakeasy';
import QRCode from 'qrcode';
import { db } from '../db';
import { users } from '../../shared/schema';
import { eq } from 'drizzle-orm';

export class TwoFactorAuthService {
  static async generateSecret(userId: number) {
    const secret = speakeasy.generateSecret({
      name: 'Family Peace Association',
      issuer: 'FPA Kenya'
    });

    await db.update(users)
      .set({ 
        twoFactorSecret: secret.base32,
        twoFactorEnabled: false 
      })
      .where(eq(users.id, userId));

    const qrCodeUrl = await QRCode.toDataURL(secret.otpauth_url || '');

    return {
      secret: secret.base32,
      qrCode: qrCodeUrl
    };
  }

  static async verifyToken(userId: number, token: string) {
    const user = await db.query.users.findFirst({
      where: eq(users.id, userId)
    });

    if (!user?.twoFactorSecret) {
      throw new Error('2FA not set up for this user');
    }

    const verified = speakeasy.totp.verify({
      secret: user.twoFactorSecret,
      encoding: 'base32',
      token
    });

    if (verified) {
      // If this is the first verification, enable 2FA
      if (!user.twoFactorEnabled) {
        await db.update(users)
          .set({ twoFactorEnabled: true })
          .where(eq(users.id, userId));
      }
      return true;
    }

    return false;
  }

  static async validateLogin(userId: number, token: string) {
    const user = await db.query.users.findFirst({
      where: eq(users.id, userId)
    });

    if (!user?.twoFactorEnabled) {
      return true; // 2FA not required
    }

    return this.verifyToken(userId, token);
  }

  static async disable(userId: number) {
    await db.update(users)
      .set({ 
        twoFactorEnabled: false,
        twoFactorSecret: null 
      })
      .where(eq(users.id, userId));
  }
}

server/services/mpesa.ts.content:
import axios from 'axios';
import { Buffer } from 'buffer';

interface MPesaConfig {
  consumerKey: string;
  consumerSecret: string;
  shortCode: string;
  passKey: string;
  callbackUrl: string;
  env: 'sandbox' | 'production';
}

class MPesaService {
  private config: MPesaConfig;
  private baseUrl: string;
  private auth = '';
  private authExpiry: number = 0;

  constructor(config: MPesaConfig) {
    this.config = config;
    this.baseUrl = config.env === 'sandbox' 
      ? 'https://sandbox.safaricom.co.ke' 
      : 'https://api.safaricom.co.ke';
  }

  private async getAuthToken(): Promise<string> {
    // Check if we have a valid cached token
    if (this.auth && Date.now() < this.authExpiry) {
      return this.auth;
    }

    const auth = Buffer.from(`${this.config.consumerKey}:${this.config.consumerSecret}`).toString('base64');

    try {
      const response = await axios.get(`${this.baseUrl}/oauth/v1/generate?grant_type=client_credentials`, {
        headers: {
          Authorization: `Basic ${auth}`,
        },
      });

      const token = response.data.access_token;
      if (!token) {
        throw new Error('No access token received from M-Pesa');
      }

      this.auth = token;
      // Token expires in 1 hour, we'll cache it for 59 minutes
      this.authExpiry = Date.now() + (59 * 60 * 1000);

      return this.auth;
    } catch (error) {
      console.error('Error getting M-Pesa auth token:', error);
      throw new Error('Failed to get M-Pesa authentication token');
    }
  }

  private generateTimestamp(): string {
    const date = new Date();
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const hour = String(date.getHours()).padStart(2, '0');
    const minute = String(date.getMinutes()).padStart(2, '0');
    const second = String(date.getSeconds()).padStart(2, '0');

    return `${year}${month}${day}${hour}${minute}${second}`;
  }

  private generatePassword(timestamp: string): string {
    const str = this.config.shortCode + this.config.passKey + timestamp;
    return Buffer.from(str).toString('base64');
  }

  public async initiateSTKPush(phoneNumber: string, amount: number, accountReference: string, transactionDesc: string) {
    try {
      const token = await this.getAuthToken();
      const timestamp = this.generateTimestamp();
      const password = this.generatePassword(timestamp);

      // Format phone number (remove leading 0 or +254)
      const formattedPhone = phoneNumber.replace(/^(0|\+254)/, '254');

      const response = await axios.post(
        `${this.baseUrl}/mpesa/stkpush/v1/processrequest`,
        {
          BusinessShortCode: this.config.shortCode,
          Password: password,
          Timestamp: timestamp,
          TransactionType: "CustomerPayBillOnline",
          Amount: amount,
          PartyA: formattedPhone,
          PartyB: this.config.shortCode,
          PhoneNumber: formattedPhone,
          CallBackURL: this.config.callbackUrl,
          AccountReference: accountReference,
          TransactionDesc: transactionDesc
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      return response.data;
    } catch (error) {
      console.error('Error initiating STK push:', error);
      throw new Error('Failed to initiate M-Pesa payment');
    }
  }

  public async checkTransactionStatus(checkoutRequestId: string) {
    try {
      const token = await this.getAuthToken();
      const timestamp = this.generateTimestamp();
      const password = this.generatePassword(timestamp);

      const response = await axios.post(
        `${this.baseUrl}/mpesa/stkpushquery/v1/query`,
        {
          BusinessShortCode: this.config.shortCode,
          Password: password,
          Timestamp: timestamp,
          CheckoutRequestID: checkoutRequestId
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      return response.data;
    } catch (error) {
      console.error('Error checking transaction status:', error);
      throw new Error('Failed to check M-Pesa transaction status');
    }
  }
}

export default MPesaService;

server/services/paypal.ts.content:
import { paypalConfig } from '../config/paypal';

interface PayPalOrder {
    id: string;
    status: string;
    intent: 'CAPTURE';
    purchase_units: Array<{
        reference_id?: string;
        amount: {
            currency_code: string;
            value: string;
        };
        description?: string;
    }>;
    create_time: string;
    links: Array<{
        href: string;
        rel: string;
        method: string;
    }>;
}

interface PayPalError {
    name: string;
    message: string;
    debug_id: string;
    details?: Array<{
        field: string;
        value: string;
        issue: string;
    }>;
}

class PayPalService {
    private accessToken: string | null = null;
    private readonly apiBase: string;

    constructor() {
        this.apiBase = process.env.PAYPAL_MODE === 'sandbox' 
            ? paypalConfig.endpoints.sandbox 
            : paypalConfig.endpoints.production;
    }

    private async getAccessToken(): Promise<string> {
        if (this.accessToken) return this.accessToken;

        if (!process.env.PAYPAL_SANDBOX_CLIENT_ID || !process.env.PAYPAL_SANDBOX_SECRET) {
            throw new Error('PayPal credentials are not configured');
        }

        const auth = Buffer.from(
            `${process.env.PAYPAL_SANDBOX_CLIENT_ID}:${process.env.PAYPAL_SANDBOX_SECRET}`
        ).toString('base64');

        const response = await fetch(`${this.apiBase}/v1/oauth2/token`, {
            method: 'POST',
            headers: {
                'Authorization': `Basic ${auth}`,
                'Content-Type': 'application/x-www-form-urlencoded',
            },
            body: 'grant_type=client_credentials'
        });

        if (!response.ok) {
            const error = await response.json();
            throw new Error(`PayPal authentication failed: ${error.error_description}`);
        }

        const data = await response.json();
        if (!data.access_token) {
            throw new Error('Failed to obtain PayPal access token');
        }
        this.accessToken = data.access_token;
        return data.access_token;
    }

    async createOrder(amount: number, currency = 'USD', description?: string): Promise<PayPalOrder> {
        const accessToken = await this.getAccessToken();

        const response = await fetch(`${this.apiBase}/v2/checkout/orders`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${accessToken}`,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                intent: 'CAPTURE',
                purchase_units: [{
                    amount: {
                        currency_code: currency,
                        value: amount.toString(),
                    },
                    description
                }]
            })
        });

        if (!response.ok) {
            const error = await response.json() as PayPalError;
            throw new Error(`Failed to create order: ${error.message}`);
        }

        return response.json();
    }

    async captureOrder(orderId: string): Promise<PayPalOrder> {
        const accessToken = await this.getAccessToken();

        const response = await fetch(`${this.apiBase}/v2/checkout/orders/${orderId}/capture`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${accessToken}`,
                'Content-Type': 'application/json',
            }
        });

        if (!response.ok) {
            const error = await response.json() as PayPalError;
            throw new Error(`Failed to capture order: ${error.message}`);
        }

        return response.json();
    }

    async verifyWebhookSignature(
        headers: Record<string, string>,
        body: string
    ): Promise<boolean> {
        const accessToken = await this.getAccessToken();

        const response = await fetch(`${this.apiBase}/v1/notifications/verify-webhook-signature`, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${accessToken}`,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                auth_algo: headers['paypal-auth-algo'],
                cert_url: headers['paypal-cert-url'],
                transmission_id: headers['paypal-transmission-id'],
                transmission_sig: headers['paypal-transmission-sig'],
                transmission_time: headers['paypal-transmission-time'],
                webhook_id: process.env.PAYPAL_SANDBOX_WEBHOOK_ID,
                webhook_event: JSON.parse(body)
            })
        });

        if (!response.ok) {
            const error = await response.json() as PayPalError;
            throw new Error(`Failed to verify webhook signature: ${error.message}`);
        }

        const result = await response.json();
        return result.verification_status === 'SUCCESS';
    }

    async handleWebhookEvent(event: any): Promise<void> {
        // Handle different webhook event types
        switch (event.event_type) {
            case 'PAYMENT.CAPTURE.COMPLETED':
                await this.handlePaymentCompleted(event);
                break;
            case 'PAYMENT.CAPTURE.DENIED':
                await this.handlePaymentDenied(event);
                break;
            case 'PAYMENT.CAPTURE.PENDING':
                await this.handlePaymentPending(event);
                break;
            default:
                console.log(`Unhandled PayPal webhook event: ${event.event_type}`);
        }
    }

    private async handlePaymentCompleted(event: any): Promise<void> {
        const { id, status, amount } = event.resource;
        // TODO: Update payment status in database
        // TODO: Send confirmation email
        console.log(`Payment ${id} completed: ${amount.value} ${amount.currency_code}`);
    }

    private async handlePaymentDenied(event: any): Promise<void> {
        const { id } = event.resource;
        // TODO: Update payment status in database
        // TODO: Send failure notification
        console.log(`Payment ${id} denied`);
    }

