import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { OptimizedImage } from "@/components/ui/optimized-image";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import AdminSidebar from '@/components/admin/Sidebar';
import { SectionLoading } from '@/components/ui/loading';
import { adminApi } from '@/lib/api';
import { Event } from '@/types/admin';
import { useToast } from '@/hooks/use-toast';

export default function AdminEvents() {
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  const {
    data: events,
    isLoading,
    error,
    refetch
  } = useQuery<Event[]>({
    queryKey: ['admin', 'events'],
    queryFn: () => adminApi.getEvents(),
  });

  const filteredEvents = events?.filter(event => {
    if (!event) return false;

    const searchLower = searchTerm.toLowerCase();
    const matchesSearch = 
      (event.name?.toLowerCase() || '').includes(searchLower) ||
      (event.description?.toLowerCase() || '').includes(searchLower) ||
      (event.location?.toLowerCase() || '').includes(searchLower);

    const matchesStatus = statusFilter === 'all' || (statusFilter === 'active' ? event.active : !event.active);

    return matchesSearch && matchesStatus;
  });

  if (error) {
    return (
      <div className="min-h-screen bg-neutral-50 flex items-center justify-center">
        <Card className="w-full max-w-md mx-4">
          <CardContent className="pt-6 text-center">
            <div className="w-16 h-16 bg-error/10 rounded-full flex items-center justify-center mx-auto mb-4">
              <i className="fas fa-exclamation-triangle text-error text-2xl"></i>
            </div>
            <h1 className="text-xl font-bold text-neutral-800 mb-2">Error Loading Events</h1>
            <p className="text-neutral-600 mb-4">
              {error instanceof Error ? error.message : 'Failed to load events'}
            </p>
            <Button onClick={() => refetch()}>
              Try Again
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      <div className="flex">
        <AdminSidebar />

        <div className="flex-1 overflow-auto">
          {/* Header */}
          <header className="bg-white shadow-sm border-b border-neutral-200 px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-neutral-800">Events</h2>
                <p className="text-neutral-600">Manage your events and registrations</p>
              </div>
              <Button>
                <i className="fas fa-plus mr-2"></i>
                New Event
              </Button>
            </div>
          </header>

          {/* Content */}
          <div className="p-6">
            <Card className="mb-6">
              <CardContent className="p-4">
                <div className="flex items-center space-x-4">
                  <Input
                    placeholder="Search events..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="max-w-sm"
                  />
                  <Select value={statusFilter} onValueChange={setStatusFilter}>
                    <SelectTrigger className="w-[180px]">
                      <SelectValue placeholder="Filter by status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Status</SelectItem>
                      <SelectItem value="active">Active</SelectItem>
                      <SelectItem value="inactive">Inactive</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </CardContent>
            </Card>

            {isLoading ? (
              <SectionLoading message="Loading events..." />
            ) : (
              <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
                {filteredEvents?.map((event) => (
                  <Card key={event.id} className="overflow-hidden">
                    <div className="relative h-48">
                          <OptimizedImage
                            src={event.imageUrl}
                        alt={event.name}
                        className="w-full h-full object-cover"
                      />
                      <div className="absolute top-2 right-2">
                        <span className={`px-2 py-1 rounded text-xs font-medium ${
                          event.active ? 'bg-green-100 text-green-800' : 'bg-gray-100 text-gray-600'
                        }`}>
                           {event.active ? 'Active' : 'Inactive'}
                         </span>
                       </div>
                     </div>

                    <CardContent className="p-4">
                  <h3 className="font-bold text-lg mb-2">{event.name}</h3>
                      <p className="text-sm text-neutral-600 mb-4 line-clamp-2">
                        {event.description}
                      </p>

                      <div className="space-y-2 text-sm text-neutral-600 mb-4">
                        <div className="flex items-center">
                          <i className="fas fa-calendar-alt w-5"></i>
                          <span>{new Date(event.date).toLocaleDateString()}</span>
                        </div>
                        <div className="flex items-center">
                          <i className="fas fa-map-marker-alt w-5"></i>
                          <span>{event.location}</span>
                        </div>
                        <div className="flex items-center">
                          <i className="fas fa-users w-5"></i>
                          <span>{event.registrations || 0} / {event.maxParticipants || 'Unlimited'} registered</span>
                        </div>
                      </div>

                      <div className="flex justify-end space-x-2">
                        <Button variant="ghost" size="sm">
                          <i className="fas fa-edit"></i>
                        </Button>
                        <Button variant="ghost" size="sm" className="text-error">
                          <i className="fas fa-trash"></i>
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin/login.tsx.content:
import { useState } from 'react';
import { useLocation, Link } from 'wouter';
import { useToast } from '@/hooks/use-toast';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { ArrowLeft } from 'lucide-react';
import { login } from '@/lib/auth';

export default function AdminLogin() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      await login(formData.username, formData.password);
      toast({
        title: 'Login Successful',
        description: 'Welcome to the admin dashboard',
      });
      setLocation('/admin/dashboard');
    } catch (error) {
      toast({
        title: 'Login Failed',
        description: 'Invalid username or password',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-neutral-50 flex items-center justify-center">
      <Card className="w-full max-w-md mx-4">
        <CardHeader className="text-center">
          <div className="flex items-center justify-between mb-4">
            <Link href="/">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Site
              </Button>
            </Link>
          </div>
          <CardTitle className="text-2xl font-bold">Admin Login</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Input
                type="text"
                placeholder="Username"
                value={formData.username}
                onChange={(e) =>
                  setFormData((prev) => ({ ...prev, username: e.target.value }))
                }
                required
              />
            </div>
            <div className="space-y-2">
              <Input
                type="password"
                placeholder="Password"
                value={formData.password}
                onChange={(e) =>
                  setFormData((prev) => ({ ...prev, password: e.target.value }))
                }
                required
              />
            </div>
            <Button
              type="submit"
              className="w-full"
              disabled={isLoading}
            >
              {isLoading ? 'Logging in...' : 'Login'}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/admin/media.tsx.content:
import { useState } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { OptimizedImage } from "@/components/ui/optimized-image";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';
import AdminSidebar from '@/components/admin/Sidebar';
import { SectionLoading } from '@/components/ui/loading';
import { useToast } from '@/hooks/use-toast';
import { adminApi } from '@/lib/api';

interface MediaItem {
  id: number;
  url: string;
  filename: string;
  type: 'image' | 'document' | 'video';
  size: number;
  uploadedAt: string;
  dimensions?: {
    width: number;
    height: number;
  };
}

export default function AdminMedia() {
  const [searchTerm, setSearchTerm] = useState('');
  const [fileType, setFileType] = useState<'all' | 'image' | 'document' | 'video'>('all');
  const { toast } = useToast();

  const { data: media, isLoading } = useQuery<MediaItem[]>({
    queryKey: ['admin', 'media'],
    queryFn: () => adminApi.getMedia(),
  });

  const uploadMutation = useMutation({
    mutationFn: (file: File) => adminApi.uploadMedia(file),
    onSuccess: () => {
      toast({
        title: 'Upload Successful',
        description: 'Your file has been uploaded.',
      });
    },
    onError: (error) => {
      toast({
        title: 'Upload Failed',
        description: error instanceof Error ? error.message : 'Failed to upload file',
        variant: 'destructive',
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (id: number) => adminApi.deleteMedia(id),
    onSuccess: () => {
      toast({
        title: 'Delete Successful',
        description: 'The file has been deleted.',
      });
    },
    onError: (error) => {
      toast({
        title: 'Delete Failed',
        description: error instanceof Error ? error.message : 'Failed to delete file',
        variant: 'destructive',
      });
    },
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      uploadMutation.mutate(file);
    }
  };

  const handleDelete = (id: number) => {
    if (window.confirm('Are you sure you want to delete this file?')) {
      deleteMutation.mutate(id);
    }
  };

  const filteredMedia = media?.filter((item) => {
    const matchesSearch = item.filename.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = fileType === 'all' || item.type === fileType;
    return matchesSearch && matchesType;
  });

  const formatFileSize = (bytes: number) => {
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    if (bytes === 0) return '0 Bytes';
    const i = Math.floor(Math.log(bytes) / Math.log(1024));
    return `${(bytes / Math.pow(1024, i)).toFixed(2)} ${sizes[i]}`;
  };

  return (
    <div className="min-h-screen bg-neutral-50">
      <div className="flex">
        <AdminSidebar />

        <div className="flex-1 overflow-auto">
          <header className="bg-white shadow-sm border-b border-neutral-200 px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-neutral-800">Media Library</h2>
                <p className="text-neutral-600">Manage your media files</p>
              </div>
              <div>
                <Input
                  type="file"
                  className="hidden"
                  id="file-upload"
                  onChange={handleFileUpload}
                  multiple
                />
                <Button asChild>
                  <label htmlFor="file-upload" className="cursor-pointer">
                    <i className="fas fa-upload mr-2"></i>
                    Upload Files
                  </label>
                </Button>
              </div>
            </div>
          </header>

          <div className="p-6">
            <Card className="mb-6">
              <CardContent className="p-4">
                <div className="flex items-center space-x-4">
                  <Input
                    placeholder="Search files..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="max-w-sm"
                  />
                  <select
                    value={fileType}
                    onChange={(e) => setFileType(e.target.value as any)}
                    className="px-3 py-2 rounded-md border border-input bg-background"
                  >
                    <option value="all">All Files</option>
                    <option value="image">Images</option>
                    <option value="document">Documents</option>
                    <option value="video">Videos</option>
                  </select>
                </div>
              </CardContent>
            </Card>

            {isLoading ? (
              <SectionLoading message="Loading media..." />
            ) : (
              <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
                {filteredMedia?.map((item) => (
                  <Card key={item.id} className="overflow-hidden">
                    <CardContent className="p-0">
                      {item.type === 'image' ? (
                        <div className="aspect-video relative group">
                          <OptimizedImage
                            src={item.url}
                            alt={item.filename}
                            className="w-full h-full object-cover"
                            loadingClassName="animate-pulse bg-gray-200"
                          />
                          <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center space-x-2">
                            <Button
                              size="sm"
                              variant="secondary"
                              onClick={() => window.open(item.url, '_blank')}
                            >
                              <i className="fas fa-eye"></i>
                            </Button>
                            <Button
                              size="sm"
                              variant="secondary"
                              onClick={() => navigator.clipboard.writeText(item.url)}
                            >
                              <i className="fas fa-link"></i>
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => handleDelete(item.id)}
                            >
                              <i className="fas fa-trash"></i>
                            </Button>
                          </div>
                        </div>
                      ) : (
                        <div className="p-4">
                          <div className="flex items-center space-x-4">
                            <i className={`fas fa-${item.type === 'document' ? 'file' : 'video'} text-2xl`}></i>
                            <div className="flex-1 min-w-0">
                              <p className="text-sm font-medium text-neutral-900 truncate">
                                {item.filename}
                              </p>
                              <p className="text-sm text-neutral-500">
                                {formatFileSize(item.size)}
                              </p>
                            </div>
                            <DropdownMenu>
                              <DropdownMenuTrigger asChild>
                                <Button variant="ghost" size="sm">
                                  <i className="fas fa-ellipsis-v"></i>
                                </Button>
                              </DropdownMenuTrigger>
                              <DropdownMenuContent align="end">
                                <DropdownMenuLabel>Actions</DropdownMenuLabel>
                                <DropdownMenuItem onClick={() => window.open(item.url, '_blank')}>
                                  <i className="fas fa-eye mr-2"></i> View
                                </DropdownMenuItem>
                                <DropdownMenuItem onClick={() => navigator.clipboard.writeText(item.url)}>
                                  <i className="fas fa-link mr-2"></i> Copy Link
                                </DropdownMenuItem>
                                <DropdownMenuSeparator />
                                <DropdownMenuItem
                                  className="text-error"
                                  onClick={() => handleDelete(item.id)}
                                >
                                  <i className="fas fa-trash mr-2"></i> Delete
                                </DropdownMenuItem>
                              </DropdownMenuContent>
                            </DropdownMenu>
                          </div>
                        </div>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin/payments.tsx.content:
import { useQuery } from '@tanstack/react-query';
import AdminSidebar from '@/components/admin/Sidebar';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useState } from 'react';
import { DateRange } from 'react-day-picker';
import { DateRangePicker } from '@/components/ui/date-range-picker';
import ExcelJS from 'exceljs';
import { saveAs } from 'file-saver';


interface Payment {
  id: string;
  orderId?: string;
  transactionId?: string;
  amount: number;
  status: string;
  createdAt: string;
  gateway: 'paypal' | 'mpesa';
  payer?: string;
}

export default function AdminPayments() {
  const [gateway, setGateway] = useState<'paypal' | 'mpesa'>('paypal');
  const [dateRange, setDateRange] = useState<DateRange | undefined>();

  // Fetch PayPal transactions
  const { data: paypalPayments, isLoading: loadingPaypal, error: errorPaypal, refetch: refetchPaypal } = useQuery<Payment[]>({
    queryKey: ['admin', 'payments', 'paypal', dateRange],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (dateRange?.from) params.append('startDate', dateRange.from.toISOString());
      if (dateRange?.to) params.append('endDate', dateRange.to.toISOString());
      const res = await fetch(`/api/admin/payments/paypal?${params.toString()}`);
      if (!res.ok) throw new Error('Failed to fetch PayPal payments');
      return res.json();
    },
  });

  // Fetch M-Pesa transactions
  const { data: mpesaPayments, isLoading: loadingMpesa, error: errorMpesa, refetch: refetchMpesa } = useQuery<Payment[]>({
    queryKey: ['admin', 'payments', 'mpesa', dateRange],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (dateRange?.from) params.append('startDate', dateRange.from.toISOString());
      if (dateRange?.to) params.append('endDate', dateRange.to.toISOString());
      const res = await fetch(`/api/admin/payments/mpesa?${params.toString()}`);
      if (!res.ok) throw new Error('Failed to fetch M-Pesa payments');
      return res.json();
    },
  });

  const handleDownload = async () => {
    const payments = gateway === 'paypal' ? paypalPayments : mpesaPayments;
    if (!payments || payments.length === 0) return;

    const workbook = new ExcelJS.Workbook();
    const worksheet = workbook.addWorksheet(`${gateway.toUpperCase()} Transactions`);

    worksheet.columns = [
      { header: 'ID', key: 'id', width: 10 },
      { header: 'Order/Transaction ID', key: 'txId', width: 35 },
      { header: 'Payer', key: 'payer', width: 30 },
      { header: 'Amount (KES)', key: 'amount', width: 15 },
      { header: 'Status', key: 'status', width: 15 },
      { header: 'Date', key: 'createdAt', width: 25 },
    ];

    const formattedData = payments.map((tx: Payment) => ({
      id: tx.id,
      txId: tx.orderId || tx.transactionId || tx.id,
      payer: tx.payer || '-',
      amount: tx.amount,
      status: tx.status,
      createdAt: new Date(tx.createdAt).toLocaleString(),
    }));

    worksheet.addRows(formattedData);

    worksheet.getRow(1).eachCell((cell) => {
      cell.font = { bold: true };
    });

    const buffer = await workbook.xlsx.writeBuffer();
    saveAs(new Blob([buffer]), `${gateway}-transactions.xlsx`);
  };

  return (

    <div className="min-h-screen bg-neutral-50">
      <div className="flex">
        <AdminSidebar />
        <div className="flex-1 overflow-auto">
          <header className="bg-white shadow-sm border-b border-neutral-200 px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-neutral-800">Payment Gateways</h2>
                <p className="text-neutral-600">View and manage PayPal & M-Pesa transactions</p>
              </div>
              <div className="flex gap-2">
                <Button variant={gateway === 'paypal' ? 'default' : 'outline'} onClick={() => setGateway('paypal')}>PayPal</Button>
                <Button variant={gateway === 'mpesa' ? 'default' : 'outline'} onClick={() => setGateway('mpesa')}>M-Pesa</Button>
              </div>
            </div>
            <div className="flex items-center justify-between mt-4">
              <DateRangePicker date={dateRange} onDateChange={setDateRange} />
              <Button onClick={handleDownload} disabled={(gateway === 'paypal' ? !paypalPayments : !mpesaPayments) || (gateway === 'paypal' ? paypalPayments?.length === 0 : mpesaPayments?.length === 0)}>
                <i className="fas fa-download mr-2"></i>
                Download Report
              </Button>
            </div>
          </header>

          <div className="p-6 space-y-8">

            {gateway === 'paypal' ? (
              <Card>
                <CardContent className="p-4">
                  <h3 className="text-lg font-semibold mb-4">PayPal Transactions</h3>
                  {loadingPaypal ? (
                    <div>Loading PayPal payments...</div>
                  ) : errorPaypal ? (
                    <div className="text-error mb-4">{errorPaypal.message}</div>
                  ) : (
                    <div className="overflow-x-auto">
                      <table className="w-full text-sm border">
                        <thead>
                          <tr>
                            <th className="text-left p-2 border-b">Order ID</th>
                            <th className="text-left p-2 border-b">Payer</th>
                            <th className="text-left p-2 border-b">Amount</th>
                            <th className="text-left p-2 border-b">Status</th>
                            <th className="text-left p-2 border-b">Date</th>
                          </tr>
                        </thead>
                        <tbody>
                          {paypalPayments?.map((tx) => (
                            <tr key={tx.id} className="border-b hover:bg-neutral-100">
                              <td className="p-2">{tx.orderId || tx.id}</td>
                              <td className="p-2">{tx.payer || '-'}</td>
                              <td className="p-2">KES {tx.amount.toLocaleString()}</td>
                              <td className="p-2">
                                <span className={`inline-block px-2 py-1 rounded text-xs font-medium ${
                                  tx.status === 'COMPLETED' ? 'bg-success/10 text-success' :
                                  tx.status === 'PENDING' ? 'bg-warning/10 text-warning' :
                                  'bg-error/10 text-error'
                                }`}>
                                  {tx.status}
                                </span>
                              </td>
                              <td className="p-2">{new Date(tx.createdAt).toLocaleString()}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                  <Button className="mt-4" onClick={() => refetchPaypal()}>Refresh</Button>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardContent className="p-4">
                  <h3 className="text-lg font-semibold mb-4">M-Pesa Transactions</h3>
                  {loadingMpesa ? (
                    <div>Loading M-Pesa payments...</div>
                  ) : errorMpesa ? (
                    <div className="text-error mb-4">{errorMpesa.message}</div>
                  ) : (
                    <div className="overflow-x-auto">
                      <table className="w-full text-sm border">
                        <thead>
                          <tr>
                            <th className="text-left p-2 border-b">Transaction ID</th>
                            <th className="text-left p-2 border-b">Payer</th>
                            <th className="text-left p-2 border-b">Amount</th>
                            <th className="text-left p-2 border-b">Status</th>
                            <th className="text-left p-2 border-b">Date</th>
                          </tr>
                        </thead>
                        <tbody>
                          {mpesaPayments?.map((tx) => (
                            <tr key={tx.id} className="border-b hover:bg-neutral-100">
                              <td className="p-2">{tx.transactionId || tx.id}</td>
                              <td className="p-2">{tx.payer || '-'}</td>
                              <td className="p-2">KES {tx.amount.toLocaleString()}</td>
                              <td className="p-2">
                                <span className={`inline-block px-2 py-1 rounded text-xs font-medium ${
                                  tx.status === 'COMPLETED' ? 'bg-success/10 text-success' :
                                  tx.status === 'PENDING' ? 'bg-warning/10 text-warning' :
                                  'bg-error/10 text-error'
                                }`}>
                                  {tx.status}
                                </span>
                              </td>
                              <td className="p-2">{new Date(tx.createdAt).toLocaleString()}</td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  )}
                  <Button className="mt-4" onClick={() => refetchMpesa()}>Refresh</Button>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin/reports.tsx.content:
import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import AdminSidebar from '@/components/admin/Sidebar';
import { SectionLoading } from '@/components/ui/loading';
import { BarChart, LineChart, PieChart } from '@/components/ui/charts';
import { adminApi } from '@/lib/api';

interface ReportData {
  donations: {
    totalAmount: number;
    byMonth: { month: string; amount: number }[];
    byCategory: { category: string; amount: number }[];
  };
  events: {
    totalAttendees: number;
    byEvent: { event: string; attendees: number }[];
    registrationTrend: { date: string; count: number }[];
  };
  volunteers: {
    totalHours: number;
    byActivity: { activity: string; hours: number }[];
    activeVolunteers: number;
  };
}

export default function AdminReports() {
  const [timeRange, setTimeRange] = useState('month');
  const [reportType, setReportType] = useState('all');

  const { data, isLoading, error } = useQuery<ReportData>({
    queryKey: ['admin', 'reports', timeRange],
    queryFn: () => adminApi.getReports(timeRange),
  });

  const handleExport = async (format: 'csv' | 'pdf') => {
    try {
      const response = await adminApi.exportReport(timeRange, format);
      // Create a download link
      const url = window.URL.createObjectURL(new Blob([response]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `report-${timeRange}.${format}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Export failed:', error);
    }
  };

  if (error) {
    return (
      <div className="min-h-screen bg-neutral-50 flex items-center justify-center">
        <Card className="w-full max-w-md mx-4">
          <CardContent className="pt-6 text-center">
            <div className="w-16 h-16 bg-error/10 rounded-full flex items-center justify-center mx-auto mb-4">
              <i className="fas fa-exclamation-triangle text-error text-2xl"></i>
            </div>
            <h1 className="text-xl font-bold text-neutral-800 mb-2">Error Loading Reports</h1>
            <p className="text-neutral-600 mb-4">
              {error instanceof Error ? error.message : 'Failed to load reports'}
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-neutral-50">
      <div className="flex">
        <AdminSidebar />

        <div className="flex-1 overflow-auto">
          <header className="bg-white shadow-sm border-b border-neutral-200 px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-2xl font-bold text-neutral-800">Reports & Analytics</h2>
                <p className="text-neutral-600">View and analyze organization performance</p>
              </div>
              <div className="flex items-center space-x-4">
                <Button variant="outline" onClick={() => handleExport('csv')}>
                  <i className="fas fa-file-csv mr-2"></i>
                  Export CSV
                </Button>
                <Button variant="outline" onClick={() => handleExport('pdf')}>
                  <i className="fas fa-file-pdf mr-2"></i>
                  Export PDF
                </Button>
              </div>
            </div>
          </header>

          <div className="p-6">
            {/* Filters */}
            <Card className="mb-6">
              <CardContent className="p-4">
                <div className="flex items-center space-x-4">
                  <Select value={timeRange} onValueChange={setTimeRange}>
                    <SelectTrigger className="w-[180px]">
                      <SelectValue placeholder="Select time range" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="week">Last Week</SelectItem>
                      <SelectItem value="month">Last Month</SelectItem>
                      <SelectItem value="quarter">Last Quarter</SelectItem>
                      <SelectItem value="year">Last Year</SelectItem>
                    </SelectContent>
                  </Select>
                  <Select value={reportType} onValueChange={setReportType}>
                    <SelectTrigger className="w-[180px]">
                      <SelectValue placeholder="Select report type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Reports</SelectItem>
                      <SelectItem value="donations">Donations</SelectItem>
                      <SelectItem value="events">Events</SelectItem>
                      <SelectItem value="volunteers">Volunteers</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </CardContent>
            </Card>

            {isLoading ? (
              <SectionLoading message="Loading reports..." />
            ) : data ? (
              <div className="grid gap-6">
                {/* Donations Section */}
                {(reportType === 'all' || reportType === 'donations') && (
                  <Card>
                    <CardHeader>
                      <CardTitle>Donations Overview</CardTitle>
                      <CardDescription>Track donation trends and patterns</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="grid md:grid-cols-2 gap-6">
                        <div className="h-[300px]">
                          <LineChart
                            data={data.donations.byMonth}
                            xField="month"
                            yField="amount"
                            title="Donations by Month"
                          />
                        </div>
                        <div className="h-[300px]">
                          <PieChart
                            data={data.donations.byCategory}
                            nameField="category"
                            valueField="amount"
                            title="Donations by Category"
                          />
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Events Section */}
                {(reportType === 'all' || reportType === 'events') && (
                  <Card>
                    <CardHeader>
                      <CardTitle>Events Performance</CardTitle>
                      <CardDescription>Track event attendance and engagement</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="grid md:grid-cols-2 gap-6">
                        <div className="h-[300px]">
                          <BarChart
                            data={data.events.byEvent}
                            xField="event"
                            yField="attendees"
                            title="Attendance by Event"
                          />
                        </div>
                        <div className="h-[300px]">
                          <LineChart
                            data={data.events.registrationTrend}
                            xField="date"
                            yField="count"
                            title="Registration Trend"
                          />
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                )}

                {/* Volunteers Section */}
                {(reportType === 'all' || reportType === 'volunteers') && (
                  <Card>
                    <CardHeader>
                      <CardTitle>Volunteer Engagement</CardTitle>
                      <CardDescription>Track volunteer activities and impact</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="grid md:grid-cols-2 gap-6">
                        <div className="h-[300px]">
                          <PieChart
                            data={data.volunteers.byActivity}
                            nameField="activity"
                            valueField="hours"
                            title="Hours by Activity"
                          />
                        </div>
                        <div className="grid grid-cols-2 gap-4">
                          <Card>
                            <CardContent className="pt-6">
                              <h3 className="text-sm font-medium text-neutral-600 mb-2">
                                Total Volunteer Hours
                              </h3>
                              <p className="text-2xl font-bold text-primary">
                                {data.volunteers.totalHours}
                              </p>
                            </CardContent>
                          </Card>
                          <Card>
                            <CardContent className="pt-6">
                              <h3 className="text-sm font-medium text-neutral-600 mb-2">
                                Active Volunteers
                              </h3>
                              <p className="text-2xl font-bold text-success">
                                {data.volunteers.activeVolunteers}
                              </p>
                            </CardContent>
                          </Card>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                )}
              </div>
            ) : null}
          </div>
        </div>
      </div>
    </div>
  );
}

